import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

export default function SignIn() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSignIn = async (e) => {
    e.preventDefault();
    if (!email || !password) {
      alert('Please enter both email and password.');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/signin', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();

      if (response.ok) {
        navigate('/main');
      } else {
        alert(`❌ ${data.error}`);
        if (data.error.includes('User does not exist')) {
          setTimeout(() => navigate('/signup'), 1000);
        }
      }
    } catch (err) {
      console.error(err);
      alert('Something went wrong. Please try again.');
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-br from-[#c2e9e3] to-[#fdfcfb] px-4">
      <div className="w-[250px] bg-white/30 backdrop-blur-md border border-white/40 p-4 rounded-2xl shadow-2xl animate-fade-in">

        {/* Logo with animation */}
        <div className="flex justify-center mb-3">
          <div className="relative animate-float">
            <img
              src="/logo.png"
              alt="Logo"
              className="w-[1px] h-[10px] object-cover rounded-full border-2 border-teal-400 shadow-md animate-pulse"
            />
            <div className="absolute inset-0 rounded-full bg-teal-300 opacity-30 blur-md z-[-1]" />
          </div>
        </div>

        <h2 className="text-lg font-bold text-teal-600 text-center mb-4">
          🌸 ManahSthiti
        </h2>

        <form onSubmit={handleSignIn} className="space-y-3 text-sm">
          <div>
            <label className="block text-gray-700 font-medium mb-1">Email</label>
            <input
              type="email"
              className="w-full border border-gray-300 rounded-md px-3 py-1.5 outline-none focus:ring-2 focus:ring-teal-400"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="you@example.com"
              required
            />
          </div>

          <div>
            <label className="block text-gray-700 font-medium mb-1">Password</label>
            <input
              type="password"
              className="w-full border border-gray-300 rounded-md px-3 py-1.5 outline-none focus:ring-2 focus:ring-teal-400"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="••••••••"
              required
            />
          </div>

          <button
            type="submit"
            className="w-full bg-teal-500 hover:bg-teal-600 text-white py-1.5 rounded-md font-semibold transition duration-200"
          >
            Sign In
          </button>
        </form>

        <div className="text-xs text-center mt-3 text-gray-600">
          <Link to="/forgot-password" className="text-teal-500 hover:underline">
            Forgot Password?
          </Link>
        </div>

        <div className="text-xs text-center mt-2 text-gray-600">
          Don’t have an account?{' '}
          <Link to="/signup" className="text-teal-500 hover:underline">
            Sign Up
          </Link>
        </div>

        <p className="text-center text-[10px] mt-4 italic text-gray-500">
          “Your mental health is a priority. Your happiness is essential.”
        </p>
      </div>

      {/* Animation styles */}
      <style>{`
        .animate-fade-in {
          animation: fadeIn 0.6s ease-in-out;
        }
        @keyframes fadeIn {
          from { opacity: 0; transform: translateY(10px); }
          to { opacity: 1; transform: translateY(0); }
        }

        .animate-float {
          animation: float 3s ease-in-out infinite;
        }
        @keyframes float {
          0%, 100% { transform: translateY(0px); }
          50% { transform: translateY(-4px); }
        }
      `}</style>
    </div>
  );
}
