import React from 'react';

export function ForgotPassword() {
  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <div className="bg-white p-8 rounded-2xl shadow-md w-80">
        <h2 className="text-2xl font-bold mb-6 text-center">Forgot Password</h2>
        <form className="space-y-4">
          <input type="email" placeholder="Enter your email" className="w-full px-4 py-2 border rounded-xl" />
          <button type="submit" className="w-full bg-yellow-500 text-white py-2 rounded-xl hover:bg-yellow-600">Reset Password</button>
        </form>
        <div className="mt-4 text-sm text-center">
          <a href="/signin" className="text-yellow-500 hover:underline">Back to Sign In</a>
        </div>
      </div>
    </div>
  );
}
