import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

export default function SignIn() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSignIn = async (e) => {
    e.preventDefault();
    if (!email || !password) {
      alert('Please enter both email and password.');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/signin', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();
      if (response.ok) {
        navigate('/main');
      } else {
        alert(`‚ùå ${data.error}`);
        if (data.error.includes('User does not exist')) {
          setTimeout(() => navigate('/signup'), 1000);
        }
      }
    } catch (err) {
      console.error(err);
      alert('Something went wrong. Please try again.');
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-br from-[#c2e9e3] to-[#fdfcfb] px-2">
      <div className="w-[400px] h-[100px] bg-white/90 backdrop-blur-lg rounded-xl shadow-md border border-gray-200 flex items-center justify-between px-4 space-x-3 animate-fade-in">
        
        {/* Small Logo */}
        <img
          src="/logo.png"
          alt="Logo"
          className="w-[50px] h-[50px] object-contain rounded-full border border-teal-300"
        />

        {/* Login Form */}
        <form onSubmit={handleSignIn} className="flex-1 flex items-center space-x-2">
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="flex-1 px-2 py-1 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-teal-400"
            placeholder="Email"
            required
          />
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="flex-1 px-2 py-1 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-teal-400"
            placeholder="Password"
            required
          />
          <button
            type="submit"
            className="bg-teal-500 hover:bg-teal-600 text-white px-3 py-1 rounded-md text-sm"
          >
            Go
          </button>
        </form>
      </div>

      <style>{`
        .animate-fade-in {
          animation: fadeIn 0.4s ease-in-out;
        }
        @keyframes fadeIn {
          from { opacity: 0; transform: translateY(10px); }
          to { opacity: 1; transform: translateY(0); }
        }
      `}</style>
    </div>
  );
}
