import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

export default function SignIn() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSignIn = async (e) => {
    e.preventDefault();

    if (!email || !password) {
      alert('Please enter both email and password.');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/signin', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();

      if (response.ok) {
        navigate('/main');
      } else {
        alert(`âŒ ${data.error}`);
        if (data.error.includes('User does not exist')) {
          setTimeout(() => navigate('/signup'), 1000);
        }
      }
    } catch (err) {
      console.error(err);
      alert('Something went wrong. Please try again.');
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-b from-[#c2e9e3] to-[#fdfcfb] px-4">
      <div className="w-full max-w-sm bg-white p-8 rounded-2xl shadow-2xl animate-fade-in">
        {/* Logo / Image */}
        <div className="flex justify-center mb-4">
          <img
            src="/logo.png" // ðŸ–¼ Replace with your logo path
            alt="Logo"
            className="w-20 h-20 object-contain rounded-full border-4 border-teal-300 shadow"
          />
        </div>

        <h2 className="text-2xl font-bold text-teal-600 text-center mb-6">
          ðŸŒ¸ Welcome to ManahSthiti
        </h2>

        <form onSubmit={handleSignIn} className="space-y-4">
          <div>
            <label className="block text-gray-700 font-medium mb-1">Email</label>
            <input
              type="email"
              className="w-full border border-gray-300 rounded-lg px-3 py-2 outline-none focus:ring-2 focus:ring-teal-400"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="you@example.com"
              required
            />
          </div>

          <div>
            <label className="block text-gray-700 font-medium mb-1">Password</label>
            <input
              type="password"
              className="w-full border border-gray-300 rounded-lg px-3 py-2 outline-none focus:ring-2 focus:ring-teal-400"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"
              required
            />
          </div>

          <button
            type="submit"
            className="w-full bg-teal-500 hover:bg-teal-600 text-white py-2 rounded-lg text-base font-semibold transition duration-200"
          >
            Sign In
          </button>
        </form>

        <div className="text-sm text-center mt-4 text-gray-600">
          <Link to="/forgot-password" className="text-teal-500 hover:underline">
            Forgot Password?
          </Link>
        </div>

        <div className="text-sm text-center mt-2 text-gray-600">
          Donâ€™t have an account?{' '}
          <Link to="/signup" className="text-teal-500 hover:underline">
            Sign Up
          </Link>
        </div>
      </div>

      {/* Tailwind custom animation (optional in your tailwind.config.js) */}
      <style>{`
        .animate-fade-in {
          animation: fadeIn 0.6s ease-in-out;
        }
        @keyframes fadeIn {
          from { opacity: 0; transform: translateY(10px); }
