import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Moon, Sun, Eye, EyeOff } from 'lucide-react';

export default function SignIn() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [darkMode, setDarkMode] = useState(false);
  const navigate = useNavigate();

  const handleSignIn = async (e) => {
    e.preventDefault();
    if (!email || !password) return alert('Please enter both email and password.');

    try {
      const response = await fetch('http://localhost:5000/api/signin', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();

      if (response.ok) {
        localStorage.setItem('userId', data.id);
        navigate('/main');
      } else {
        alert(`‚ùå ${data.error}`);
        if (data.error.includes('User does not exist')) {
          setTimeout(() => navigate('/signup'), 1000);
        }
      }
    } catch (err) {
      console.error(err);
      alert('Something went wrong. Please try again.');
    }
  };

  return (
    <div
      className={`${
        darkMode ? 'bg-[#0f172a]' : 'bg-gradient-to-br from-[#c2e9e3] to-[#fdfcfb]'
      } min-h-screen w-full flex flex-col items-center justify-start pt-24 px-6 transition-colors duration-300`}
    >
      {/* Theme toggle */}
      <div className="absolute top-4 right-4 cursor-pointer" onClick={() => setDarkMode(!darkMode)}>
        {darkMode ? <Sun className="w-5 h-5 text-yellow-300" /> : <Moon className="w-5 h-5 text-gray-700" />}
      </div>

      {/* Logo */}
      <div className="relative w-12 h-12 animate-float mb-3">
        <img
          src="/logo.gif"
          alt="Logo"
          className="w-full h-full object-cover rounded-full border border-teal-400 shadow-md animate-pulse"
        />
        <div className="absolute inset-0 rounded-full bg-teal-300 opacity-20 blur-sm z-[-1]" />
      </div>

      <h2 className="text-lg font-semibold text-teal-600 dark:text-teal-400 text-center mb-5">üå∏ ManahSthiti</h2>

      {/* Form */}
      <form
        onSubmit={handleSignIn}
        className="w-full max-w-sm space-y-5 animate-fade-in"
      >
        <div>
          <label className="block text-sm mb-1 text-gray-700 dark:text-gray-200">Email</label>
          <input
            type="email"
            className="w-full border border-gray-300 rounded-lg px-4 py-2 text-sm outline-none focus:ring-2 focus:ring-teal-400 dark:bg-gray-800 dark:text-white"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="you@example.com"
            required
          />
        </div>

        <div className="relative">
          <label className="block text-sm mb-1 text-gray-700 dark:text-gray-200">Password</label>
          <input
            type={showPassword ? 'text' : 'password'}
            className="w-full border border-gray-300 rounded-lg px-4 py-2 text-sm outline-none focus:ring-2 focus:ring-teal-400 dark:bg-gray-800 dark:text-white"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
            required
          />
          <span
            className="absolute right-4 top-10 transform -translate-y-1/2 cursor-pointer text-gray-500"
            onClick={() => setShowPassword(!showPassword)}
          >
            {showPassword ? <EyeOff size={16} /> : <Eye size={16} />}
          </span>
        </div>

        <button
          type="submit"
          className="w-full bg-teal-500 hover:bg-teal-600 text-white py-2 rounded-lg text-sm font-medium transition duration-200"
        >
          Sign In
        </button>

        <div className="text-xs text-center mt-2 text-gray-600 dark:text-gray-400">
          <Link to="/forgot-password" className="text-teal-500 hover:underline">Forgot Password?</Link>
        </div>

        <div className="text-xs text-center mt-1 text-gray-600 dark:text-gray-400">
          Don‚Äôt have an account?{' '}
          <Link to="/signup" className="text-teal-500 hover:underline">Sign Up</Link>
        </div>

        <p className="text-center text-xs mt-6 italic text-gray-500 dark:text-gray-400">
          ‚ÄúYour mental health is a priority.‚Äù
        </p>
      </form>

      {/* Animations */}
      <style>{`
        .animate-fade-in {
          animation: fadeIn 0.6s ease-in-out;
        }
        @keyframes fadeIn {
          from { opacity: 0; transform: translateY(10px); }
          to { opacity: 1; transform: translateY(0); }
        }
        .animate-float {
          animation: float 3s ease-in-out infinite;
        }
        @keyframes float {
          0%, 100% { transform: translateY(0); }
          50% { transform: translateY(-6px); }
        }
      `}</style>
    </div>
  );
}
